typedef struct animation_frame
{
    uint8_t        pos_x;
    uint8_t        pos_y;
    uint8_t        width;
    uint8_t        height;
    uint8_t        delay;
    const uint8_t *bitmap;
} animation_frame;

// Animation frames
animation_frame frames[] = {
    {7, 103, 24, 32, 100, &bitmap_mario_0[0]}, {5, 103, 28, 30, 100, &bitmap_mario_1[0]},
    {4, 80, 32, 32, 100, &bitmap_mario_2[0]},  {4, 40, 32, 32, 100, &bitmap_mario_2[0]},
    {4, 0, 32, 32, 100, &bitmap_mario_2[0]},   {3, 20, 34, 34, 100, &bitmap_mario_3[0]},
    {2, 40, 36, 40, 100, &bitmap_mario_4[0]},  {2, 60, 36, 40, 100, &bitmap_mario_4[0]},
    {2, 80, 36, 40, 100, &bitmap_mario_4[0]},  {7, 103, 24, 32, 100, &bitmap_mario_0[0]},
};

int main(void)
{
#ifdef PLATFORMIO  // Use PlatformIO CH32V
    Delay_Init();
#else  // Use ch32v003fun
    SystemInit();
    Delay_Ms(100);
#endif

    tft_init();

    tft_set_background_color(BLACK);
    tft_fill_rect(0, 0, ST7789_WIDTH, ST7789_HEIGHT, BLACK);

    uint8_t          frame = 0;
    uint8_t          shift = 0;
    uint32_t         count = 0;
    animation_frame *p_frame;

    while (1)
    {
        tft_set_color(RED);
        tft_set_cursor(174, 2);
        tft_print("Go Mario!!!");
        tft_set_color(BLUE);
        tft_set_cursor(204, 12);
        tft_print("Run!!!");
        tft_set_color(ORANGE);
        tft_set_cursor(162, 22);
        tft_print("Hit Bricks!!!");
        tft_set_color(PURPLE);
        tft_set_cursor(162, 32);
        tft_print("Beat Monsters");
        tft_set_color(PINK);
        tft_set_cursor(150, 42);
        tft_print("Rescue Princess");
        tft_set_color(WHITE);
        tft_set_cursor(204, 128);
        tft_print("Frames");
        tft_set_cursor(132, 128);
        tft_print_number(count++, 65);

        p_frame = &frames[frame];

        tft_draw_bitmap(p_frame->pos_x + shift, p_frame->pos_y, p_frame->width, p_frame->height, p_frame->bitmap);
        Delay_Ms(p_frame->delay);
        // TODO: only erase the delta between frames
        tft_fill_rect(p_frame->pos_x + shift, p_frame->pos_y, p_frame->width, p_frame->height, BLACK);

        if (frame % 9)
        {
            shift += 4;
            if (shift >= 200)
            {
                shift = 0;
            }
        }

        if (++frame >= 10)
        {
            frame = 0;
        }
    }
}
